import React, { useEffect, useState } from "react";
import "../node_modules/bootstrap/dist/css/bootstrap.min.css"
import "../node_modules/bootstrap-icons/font/bootstrap-icons.css"
import { Hapiee } from "./Signup";
import { list } from "./Yash";



export const Veedu=()=>
{
    const[tmpArray,settmpArray]=useState([])
    const[tempArray,settempArray]=useState(false)
    const[readview,setreadview]=useState(false)



    const hello=()=>{
        settmpArray(list())
    }
    useEffect(()=>
    {
        hello()
    },[])

    return(
        <>
        {/* <button className="btn btn-info" onClick={hello}>view</button> */}
        
        {(tempArray)?
                <>
                    <Hapiee/>
                    <button className="btn btn-outline-secondary" onClick={
                        ()=>{
                            settempArray(false)
                        }
                    }>
                        Back
                    </button>
                </>
                :
                (readView)?
                <>
                <Read who={pos}/>
                    <button className="btn btn-outline-secondary" onClick={
                        ()=>{
                            setreadView(false)
                        }
                    }>
                        <i class="bi bi-skip-backward-btn-fill"></i> Back
                    </button>
                </>
                :
                <>
                    <button className="btn btn-outline-success mb-3"
                        onClick={()=>{
                            settempArray(true)
                        }}>
                        Recruite New Employee
                    </button>
        

        <div className="row justify-content-center">
                        <div className="table-responsive-md">
                            <table className="col-lg-8 col-md-10 col-sm-12 table table-striped p-3 shadow rounded">
                                <thead>
                                    <tr>
                                        <th>User Name</th>
                                        <th>User Age</th>
                                        <th>User Salary</th>
                                        <th>User Place </th>
                                        <th>User Skills</th>
                                    </tr>
                                </thead>
                                <tbody>
                                    {tmpArray.map((ele)=>(
                                        <tr>
                                            <td>{ele.Fname}</td>
                                            <td>{ele.Young}</td>
                                            <td>{ele.Sal}</td>
                                            <td>{ele.City1}</td>
                                            <td>{ele.Yes}</td>
                                        </tr>
                                    ))}
                                </tbody>
                            </table>
                        </div>
                    </div>
                    </>
}
        </>
    )
}
